import 'antd/dist/reset.css';
import React, { useState, useEffect } from 'react';
import {
  Layout,
  Typography,
  Tag,
  Carousel,
  Divider,
  Menu,
  Drawer,
  Button,
  Card,
  Space,
  Avatar,
  FloatButton,
  Modal
} from 'antd';
import {
  EnvironmentOutlined,
  MailOutlined,
  PhoneOutlined,
  GithubOutlined,
  TrophyOutlined,
  UserOutlined,
  SafetyCertificateOutlined,
  BookOutlined,
  LaptopOutlined,
  ProjectOutlined,
  TrophyFilled,
  MenuOutlined,
  StarOutlined,
  FacebookFilled,
  UpOutlined,
  CodeOutlined,
  ToolOutlined,
  TeamOutlined,
  CommentOutlined
} from '@ant-design/icons';

const { Header, Sider, Content } = Layout;
const { Title, Text, Paragraph } = Typography;

const Portfolio = () => {
  const [activeSection, setActiveSection] = useState('about');
  const [mobileDrawerVisible, setMobileDrawerVisible] = useState(false);
