              <div className="flex items-center mb-8">
                <div className="w-10 h-10 bg-gradient-to-br from-[#00ADB5] to-[#009ca7] rounded-full flex items-center justify-center mr-4 shadow-md">
                  <TrophyOutlined className="text-white text-lg" />
                </div>
                <Title level={2} className="text-2xl sm:text-3xl font-bold text-[#222831] mb-0 tracking-tight">
                  Achievements
                </Title>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {achievements.map((achievement, index) => (
                  <Card
                    key={index}
                    hoverable
                    className="rounded-xl shadow-sm hover:shadow-md transition-all flex flex-col h-full"
                    bodyStyle={{ display: "flex", flexDirection: "column", height: "100%" }}
                  >
                    <div className="flex items-start mb-2">
                      <div className="w-10 h-10 bg-gradient-to-br from-[#00ADB5] to-[#009ca7] rounded-full flex items-center justify-center mr-4 shadow-md">
                        {achievement.icon}
                      </div>
                      <div>
                        <Title level={4} className="text-lg sm:text-xl font-bold mb-1 text-[#222831]">
                          {achievement.title}
                        </Title>
                      </div>
                    </div>
                    <Text className="text-gray-600 text-base mb-2 flex-1">
                      {achievement.description}
                    </Text>
                    <div 
                      className="mt-auto cursor-pointer" 
                      onClick={() => {
                        setSelectedCertificate(achievement);
                        setIsModalVisible(true);
                      }}
                    >
                      <img
                        src={achievement.certificate}
                        alt={`${achievement.title} Certificate`}
                        className="rounded-lg shadow max-h-40 w-auto object-contain mx-auto"
                      />
                    </div>
                  </Card>
                ))}
              </div>
            </section>

            {/* Contact */}
            <section id="contact" className="mb-20 scroll-mt-20">
              <div className="flex items-center mb-8">
                <div className="w-10 h-10 bg-[#00ADB5] rounded-full flex items-center justify-center mr-4">
                  <CommentOutlined className="text-white text-lg" />
                </div>
                <Title level={2} className="text-2xl sm:text-3xl font-bold text-[#222831] mb-0">
                  Get In Touch
                </Title>
              </div>
              <Card className="rounded-xl shadow-sm p-6 bg-gradient-to-r from-[#00adb510] to-[#00adb520]">
                <div className="flex flex-col md:grid md:grid-cols-2 gap-8">
                  <div>
                    <Title level={4} className="text-lg sm:text-xl font-bold mb-4">
                      Contact Information
                    </Title>
                    <div className="space-y-6">
                      <div className="flex items-start">
                        <MailOutlined className="text-[#00ADB5] text-xl mr-3 mt-1" />
                        <div>
                          <Text strong className="block text-base">
                            Email
                          </Text>
                          <Text className="text-gray-600 text-base">
                            markanthonyaguirre1234@gmail.com
                          </Text>
                          <Button
                            type="text"
                            className="text-[#00ADB5] p-0 hover:text-[#00D4DD] text-base"
                            href="mailto:markanthonyaguirre1234@gmail.com"
                          >
                            Send Message
                          </Button>
                        </div>
                      </div>
                      <div className="flex items-start">
                        <PhoneOutlined className="text-[#00ADB5] text-xl mr-3 mt-1" />
                        <div>
                          <Text strong className="block text-base">
                            Phone
                          </Text>
                          <Text className="text-gray-600 text-base">
                            +63 993 425 3793
                          </Text>
                          <Button
                            type="text"
                            className="text-[#00ADB5] p-0 hover:text-[#00D4DD] text-base"
                            href="tel:+639934253793"
                          >
                            Call Now
                          </Button>
                        </div>
                      </div>
                      <div className="flex items-start">
                        <EnvironmentOutlined className="text-[#00ADB5] text-xl mr-3 mt-1" />
                        <div>
                          <Text strong className="block text-base">
                            Location
                          </Text>
                          <Text className="text-gray-600 text-base">
                            Borseth, Alangalang, Leyte, Philippines
                          </Text>
                          <Button
                            type="text"
                            className="text-[#00ADB5] p-0 hover:text-[#00D4DD] text-base"
                            href="https://maps.google.com/?q=Borseth,Alangalang,Leyte"
                            target="_blank"
                          >
                            View on Map
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div>
                    <Title level={4} className="text-lg sm:text-xl font-bold mb-4">
                      Connect With Me
                    </Title>
                    <div className="flex flex-wrap gap-4 mb-6">
                      <Button
                        shape="circle"
                        icon={<GithubOutlined />}
                        href="https://github.com/Maarrrkkkkk"
                        target="_blank"
                        className="bg-white border border-gray-200 text-gray-700 hover:border-[#00ADB5] hover:text-[#00ADB5] w-12 h-12"
                        aria-label="GitHub"
                      />
                      <Button
                        shape="circle"
                        icon={<FacebookFilled />}
                        href="https://www.facebook.com/share/1ASRfELTm3/"
                        target="_blank"
                        className="bg-white border border-gray-200 text-gray-700 hover:border-[#00ADB5] hover:text-[#00ADB5] w-12 h-12"
                        aria-label="Facebook"
                      />
                      <Button
                        shape="circle"
                        icon={<MailOutlined />}
                        href="mailto:markanthonyaguirre1234@gmail.com"
                        className="bg-white border border-gray-200 text-gray-700 hover:border-[#00ADB5] hover:text-[#00ADB5] w-12 h-12"
                        aria-label="Email"
                      />
                    </div>
                    <Title level={4} className="text-lg sm:text-xl font-bold mb-4">
                      Availability
                    </Title>
                    <div className="flex items-center">
                      <div className="w-3 h-3 bg-green-400 rounded-full mr-2 animate-pulse"></div>
                      <Text className="text-base">Currently open to new opportunities</Text>
                    </div>
                  </div>
                </div>
              </Card>
            </section>

            {/* Footer */}
            <div className="text-center py-8 border-t border-gray-200">
              <Text className="text-gray-500 text-base">
                Â© {new Date().getFullYear()} Mark Anthony Aguirre. All rights reserved.
              </Text>
            </div>
          </div>
        </Content>
      </Layout>

      {/* Floating Action Button */}
      <FloatButton.BackTop
        icon={<UpOutlined />}
        visibilityHeight={300}
        type="primary"
        className="!bg-[#00ADB5] !border-none w-12 h-12"
      />

      {/* Certificate Modal */}
      <Modal
        open={isModalVisible}
        footer={null}
        onCancel={() => setIsModalVisible(false)}
        centered
        width={800}
      >
        {selectedCertificate && (
          <div className="text-center">
            <Title level={4} className="mb-4 text-xl">{selectedCertificate.title}</Title>
            <img
              src={selectedCertificate.certificate}
              alt={`${selectedCertificate.title} Certificate`}
              className="w-full h-auto rounded-lg shadow-lg"
            />
          </div>
        )}
      </Modal>
    </Layout>
  );
};

export default Portfolio;
