import 'antd/dist/reset.css';
import React, { useState, useEffect } from 'react';
import {
  Layout,
  Typography,
  Tag,
  Carousel,
  Divider,
  Menu,
  Drawer,
  Button,
  Card,
  Space,
  Avatar,
  FloatButton,
  Modal
} from 'antd';
import {
  CalendarOutlined,
  EnvironmentOutlined,
  MailOutlined,
  PhoneOutlined,
  GithubOutlined,
  TrophyOutlined,
  UserOutlined,
  SafetyCertificateOutlined,
  BookOutlined,
  LaptopOutlined,
