sourcesContent:
import 'antd/dist/reset.css';
import React, { useState, useEffect } from 'react';
import {
  Layout,
  Typography,
  Tag,
  Carousel,
  Divider,
  Menu,
  Drawer,
  Button,
  Card,
  Space,
  Avatar,
  FloatButton,
  Modal,
  Form,
  Input
} from 'antd';
import {
  EnvironmentOutlined,
  MailOutlined,
  PhoneOutlined,
  GithubOutlined,
  TrophyOutlined,
  UserOutlined,
  SafetyCertificateOutlined,
  BookOutlined,
  LaptopOutlined,
  ProjectOutlined,
  TrophyFilled,
  MenuOutlined,
  StarOutlined,
  FacebookFilled,
  UpOutlined,
  CodeOutlined,
  ToolOutlined,
  TeamOutlined,
  CommentOutlined
} from '@ant-design/icons';

const { Header, Sider, Content } = Layout;
const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;

const Portfolio = () => {
  const [activeSection, setActiveSection] = useState('about');
  const [mobileDrawerVisible, setMobileDrawerVisible] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [selectedCertificate, setSelectedCertificate] = useState(null);
  const [form] = Form.useForm();

  const scrollToSection = (sectionId) => {
    setActiveSection(sectionId);
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
    setMobileDrawerVisible(false);
  };

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);

      const sections = ['about', 'skills', 'education', 'projects', 'portfolio', 'achievements', 'contact'];
      for (const section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          if (rect.top <= 100 && rect.bottom >= 100) {
            setActiveSection(section);
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const menuItems = [
    { key: 'about', icon: <UserOutlined />, label: 'About' },
    { key: 'skills', icon: <CodeOutlined />, label: 'Skills' },
    { key: 'education', icon: <BookOutlined />, label: 'Education' },
    { key: 'projects', icon: <LaptopOutlined />, label: 'Projects' },
    { key: 'portfolio', icon: <ProjectOutlined />, label: 'Portfolio' },
    { key: 'achievements', icon: <TrophyFilled />, label: 'Achievements' },
    { key: 'contact', icon: <MailOutlined />, label: 'Contact' },
  ];

  const skills = {
    languages: ['HTML', 'CSS', 'JavaScript'],
    frameworks: ['Bootstrap', 'Tailwind CSS'],
    tools: ['Git', 'GitHub', 'VS Code'],
    softSkills: [
      'Works Under Pressure',
      'Team Collaboration',
      'Time Management',
      'Communication'
    ]
  };

  const achievements = [
    {
      icon: <SafetyCertificateOutlined className="text-white text-lg" />,
      title: "Academic Achiever",
      description: "Consistently recognized for academic excellence and high performance.",
      certificate: "/academic-achiever.jpg"
    },
    {
      icon: <TrophyOutlined className="text-white text-lg" />,
      title: "Best in Thesis/Capstone Project",
      description: "Our capstone project, recognized as one of the best in our batch...",
      certificate: "/best-capstone.jpg"
    },
    {
      icon: <UserOutlined className="text-white text-lg" />,
      title: "Outstanding On-The-Job Training (Intern)",
      description: "Developed for EVSU â€“ Graduate School, this platform functions...",
      certificate: "/ojt.jpg"
    },
    {
      icon: <StarOutlined className="text-white text-lg" />,
      title: "Dean's Lister",
      description: "Maintained academic excellence throughout the degree program.",
      certificate: "/deans-lister.jpg"
    }
  ];

  const projects = [
    {
      title: "Capstone Project Dashboard",
      description: "A smart academic platform recognized among the top capstone projects, featuring automated grading, advisor recommendation, and scheduling to streamline faculty-student collaboration.",
      images: ["/capstone-login.jpg", "/capstone-project.jpg", "/capstone-project2.jpg"],
      tags: ["Bootstrap", "Django", "JavaScript"],
      link: "#",
      icon: <TrophyOutlined />
    },
    {
      title: "Academic Archive System",
      description: "A digital repository developed for EVSU Graduate School, allowing students to upload theses, dissertations, and re-entry papers with features like citation tracking, download monitoring, and audit logging.",
      images: ["/gs-project-login.jpg", "/gs-project.jpg", "/gs-project2.jpg", "/gs-project3.jpg", "/gs-project4.jpg"],
      tags: ["Bootstrap", "Django", "JavaScript"],
      link: "#",
      icon: <BookOutlined />
    }
  ];

  const renderProfileSection = () => (
    <div className="flex flex-col items-center mb-8">
      <div className="relative group profile-container">
        <div className="w-28 h-28 rounded-full overflow-hidden border-2 border-[#00ADB5] shadow-xl transition-all duration-500 group-hover:border-[#00D4DD] group-hover:shadow-2xl">
          <img
            src="/profile.jpg"
            alt="Profile"
            className="w-full h-full object-cover object-top transition-transform duration-500 group-hover:scale-110"
          />
        </div>
        <div className="profile-glow group-hover:shadow-[0_0_24px_6px_rgba(0,173,181,0.4)]"></div>
      </div>
      <div className="text-center mt-4">
        <Title level={3} className="text-2xl font-bold text-[#222831] mb-1">
          Mark Anthony Aguirre
        </Title>
        <Text className="text-base text-[#00ADB5] font-medium">
          Front-end Web Builder
        </Text>
      </div>
    </div>
  );

